import React, { useEffect, useState } from "react";
import { FaCode } from "react-icons/fa";
import { Icon, Col, Card, Row, Carousel } from "antd";
import ImageSlider from "../../Utils/ImageSlider";
import CheckBox from ".CheckBox";

//To get the data from DB, use axios
import axios from "axios";

const { Meta } = Card;

function LandingPage() {
  const [Products, setProducts] = useState([]);
  const [Skip, setSkip] = useState(0);
  const [Limit, setLimit] = useState(8);

  useEffect(() => {
    // To make a body make rendering 8 items at first

    let body = {
      skip: Skip,
      limit: Limit,
    };

    getProducts(body);
  }, []);

  const getProducts = (body) => {
    //request product data (check the route in the back-end)
    axios.post("api/product/products", body).then((response) => {
      if (response.data.success) {
        if(body.loadMore) {
            setProducts([...Products,...response.data.productInfo])
        } else {
            setProducts(response.data.productInfo);
        }

      } else {
        alert("fail to get the data");
      }
    });
  };

  const loadMoreHandler = () => {
    let skip = Skip + Limit;

    let body = {
      skip: skip,
      limit: Limit,
      loadMore: true
    };

    getProducts(body);
    setSkip(skip);
  };

  const renderCards = Products.map((product, index) => {
    console.log("product", product);
    return (
      <Col lg={6} md={8} xs={24} key={index}>
        <Card cover={<ImageSlider images={product.images} />}>
          <Meta title={product.title} description={`$${product.price}`} />
        </Card>
      </Col>
    );
  });

  return (
    <div style={{ width: "75%", margin: "3rem auto" }}>
      <div style={{ textAlign: "center" }}>
        <h2>
          Let's Travel Anywhere <Icon type="rocket" />{" "}
        </h2>
      </div>

      {/* Filter */}

      {/*CheckBox */}
        <CheckBox />
      {/* Search */}

      {/* Card */}

      <Row gutter={[16, 16]}>{renderCards}</Row>

      <br />

      <div style={{ display: "flex", justifyContent: "center" }}>
        <button onClick={loadMoreHandler}> Show more </button>
      </div>
    </div>
  );
}

export default LandingPage;
