import React,{ useEffect} from 'react'

import Jumbotron from "../Jumbotron/Jumbotron";
const SpeechRecognition =
  window.SpeechRecognition || window.webkitSpeechRecognition;
const recognition = new SpeechRecognition();


function FirstPage() {

    const voiceCommands = () => {
        recognition.start();
        // On start
        recognition.onstart = () => {
          console.log("Voice is actived");
        };
        // Recognize the voice
        recognition.onresult = (e) => {
          let current = e.resultIndex;
          let userVoice = e.results[current][0].transcript;
          console.log(userVoice);

          if (userVoice === "go home") {
            goToHome();
          } else if (userVoice === "reservation") {
            goToReservation();
          } else if (userVoice === "table") {
            goToTable();
          } else {
            alert(
              "Sorry, we are not support other voice instructions Sorry for your inconvenience ðŸ¤—"
            );
          }

          setTimeout(() => {
            recognition.start();
          }, 2000);

        };
      };

      function goToHome() {
        window.location = "http://localhost:3000/Home";
      }
      function goToReservation() {
        window.location = "http://localhost:3000/Reservation";
      }

      function goToTable() {
        window.location = "http://localhost:3000/Table";
      }

      useEffect(()=>{
        voiceCommands();
      })

    return (
        <>

            <Jumbotron />
                <span style={{ fontSize: '2rem' }}>Welcome to Guu Garden! :) </span>
        </>
    )
}

export default FirstPage
