import React, { useState } from "react";
import {Typography, Button, Form, Input} from 'antd';
import FileUpload from "../../Utils/FileUpload";

const {Title} = Typography;
const {TextArea} = Input;

const Continents = [
  { key: 1, value: "Africa" },
  { key: 2, value: "Europe" },
  { key: 3, value: "Asia" },
  { key: 4, value: "North America" },
  { key: 5, value: "South America" },
  { key: 6, value: "Australia" },
  { key: 7, value: "Antarctica" },
];

function UploadProductPage(props) {


  const [Title, setTitleValue] = useState("");
  const [Description, setDescriptionValue] = useState("");
  const [Price, setPriceValue] = useState(0);
  const [Continent,setContinentValue] = useState(1);
  const [Images, setImages] = useState([]);

  const onTitleChange = (event) => {
    setTitleValue(event.currentTarget.value);
  };

  const onDescriptionChange = (event) => {
    setDescriptionValue(event.currentTarget.value);
  };

  const onPriceChange = (event) => {
    setPriceValue(event.currentTarget.value);
  };

  const onContinentsSelectChange = (event) => {
    setContinentValue(event.currentTarget.value);
  }

  const updateImages =(newImages) => {
    setImages(newImages);
  }

  const submitHandler = (event) => {
    event.preventDefault();

    if(!Title || ! Description || !Price || !Continents || !Image) {
      return alert("Please fill up all info");
    }

    // Send a filled out data into a server by request
    const body ={
      //Id who log-in
      writer: props.user.userData._id,
      title:Title,
      description: Description,
      price:Price,
      imges: Images,
      continents: Continents
    }

    axios.post("/api/product",)
  }

  return (
    <div style={{ maxWidth: '700px', margin: '2rem auto' }}>
        <div style={{ textAlign: 'center', marginBottom: '2rem' }}>
            <Title level={2}> Upload Travel Product</Title>
        </div>


        <Form onSubmit= {submitHandler}>

            {/* DropZone */}
            <FileUpload refreshFunction={updateImages}/>

            <br />
            <br />
            <label>Title</label>
            <Input
                onChange={onTitleChange}
                value={Title}
            />
            <br />
            <br />
            <label>Description</label>
            <TextArea
                onChange={onDescriptionChange}
                value={Description}
            />
            <br />
            <br />
            <label>Price($)</label>
            <Input
                onChange={onPriceChange}
                value={Price}
                type="number"
            />
            <br /><br />
            <select onChange={onContinentsSelectChange} value={Continent}>
                {Continents.map(item => (
                    <option key={item.key} value={item.key}>{item.value} </option>
                ))}
            </select>
            <br />
            <br />

            <Button type="submit">
                Submit
            </Button>

        </Form>

    </div>
)
}

export default UploadProductPage;
