import React from "react";

function renderWeatherHTML({temperature,weather,img}) {
    return `<h2>${new Date().toLocaleDateString()}</h2>
            <h2>Today Temperature:`+{temperature}+`&deg;
            <h2>Today Weather: ${currentDay.weather[0].description}</h2>
            <img src="https://openweathermap.org/img/wn/${currentDay.weather[0].icon}@2x.png">`;
};

const kelvinToCelsius = k => {
    return (k - 273.15).toFixed(0);
};

export default renderWeatherHTML;
