import React,{useState,useEffect} from 'react';
import axios from "axios";
import "./orderListPage.css";
import EmployeeNavigation from "../EmployeeNavigation";
function OrderListPage() {

    const [OrderList, setOrderList] = useState([]);

    // get the order list Data 
    useEffect(()=> {
        fetchOrderList();
    },[]);

    const fetchOrderList = () => {
        axios.post("/api/order/list")
        .then(response=>{
            if(response.data.success) {
                console.log(response.data);
                setOrderList(response.data.info);

            }else {
                alert("Failed to get the response data ");
            }
        });

    }

    const renderTableBody = OrderList.map((order,index)=> {
        return (
            <tr key={index}>
                <td>{order.tableName}</td>
                <td>{order.ItemName}</td>
                <td>{order.numOfOrder}</td>
                <td>Confirm the Order</td>
                <td>Cancele the order</td>
            </tr>
        )
    })


    return (


        <div style={{width:"100%", margin: '3rem auto'}}>
        <EmployeeNavigation />
        <div style={{ textAlign: "center" }}>
        <h2>
          Check Orders Just Arrive! 
        </h2>
      </div>

        <table>
            <thead>
            <tr>
                <th>Table Name</th>
                <th>Dish/Drink Name</th>
                <th>#</th>
                <th>Confirmation</th>
                <th>Cancelation</th>
            </tr>
            </thead>
            <tbody>
                {renderTableBody}
            </tbody>
        </table>
        </div>
    )
}

export default OrderListPage
