import React from "react";
import { Button, Descriptions, InputNumber, Typography } from "antd";
const { Title } = Typography;
function ProductInfo(props) {
  const clickHandler = () => {
    // Add  necessary data into Cart field
  };

  return (
    <div>
      <Descriptions title="Product Info">
        <Descriptions.Item label="Price">
          {props.detail.price}{" "}
        </Descriptions.Item>
        <Descriptions.Item label="Sold">{props.detail.sold}</Descriptions.Item>
        <Descriptions.Item label="View">{props.detail.view}</Descriptions.Item>
        <Descriptions.Item label="Description">
          {props.detail.description}
        </Descriptions.Item>
      </Descriptions>

      <br />

      <br />
      <div style={{ maxWidth: "400px", margin: "1rem auto" }}>
        <div style={{ textAlign: "center", marginBottom: "1rem" }}>
          <Title level={2}>Make Order!</Title>
        </div>
        <lable>Number of Order</lable>
        <InputNumber min={1} max={10} defaultValue={1}  />
        <label>Your Table: </label>
        <Input />
        <div style={{ display: "flex", justifyContent: "center" }}>
          <Button
            size="large"
            shape="round"
            type="danger"
            onClick={clickHandler}
          >
            Make Order
          </Button>
        </div>
      </div>
    </div>
  );
}

export default ProductInfo;
