import React from "react";

// Set the Weather API
const openWeatherKey = "fd3ee3aacf7b96f369c35f9b11b46187";
const weatherUrl = "https://api.openweathermap.org/data/2.5/weather";

// Page elements

const $weatherDiv = ('#weather1');

const getWeather = async (e) => {
    e.preventDefault();
  console.log("Check");
  try {
    // Set the request url
    const location = "Vancouver";
    const urlToFetch = `${weatherUrl}?&q=${location}&APPID=${openWeatherKey}`;

    //Get the response
    const response = await fetch(urlToFetch);
    // Check the status of response
    if (response.ok) {
      const jsonResponse = await response.json();
      console.log(jsonResponse);
      return jsonResponse;
    }
  } catch (error) {
    console.log(error);
  }
};

const renderWeatherHTML = (currentDay) => {
    return `<h2>${new Date().toLocaleDateString()}</h2>
            <h2>Today Temperature: ${kelvinToCelsius(currentDay.main.temp)}&deg;
            <h2>Today Weather: ${currentDay.weather[0].description}</h2>
            <img src="https://openweathermap.org/img/wn/${currentDay.weather[0].icon}@2x.png">`;
};

const kelvinToCelsius = k => {
    return (k - 273.15).toFixed(0);
};

const renderWeather = (day) => {
    let weatherHTMLContent = renderWeather(day);
    $weatherDiv.append(weatherHTMLContent);
}

const executeAction = (e) => {
    getWeather().then(forecast => renderWeather(forecast));
    e.preventDefault();
}

function Home() {
  return (
    <>
        <main>
        <h1> Do you want to know a special menu only for today's weather?</h1>
            <form autoComplete="off">
                <button id ='button' type="submit" onClick={executeAction}>Go Check!</button>
            </form>
        </main>

      <div className="container">
        <h1> This is Home page</h1>
        <div id="destination"></div>
        <div className="sectiontitle">
          <h2>TODAY WEATHER</h2>
        </div>
        <section id="weather">
          <div className="weather" id="weather1"></div>
        </section>
      </div>
    </>
  );
}

export default Home;
