import React ,{useEffect}from 'react';
import Jumbotron from "./Jumbotron";
const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
const recognition = new SpeechRecognition();
recognition.start();

function Main() {
    const voiceCommands = () => {
        // On start
        recognition.onstart = () => {
          console.log('Voice is actived');
        };
      }

      // Recognize the voice
      recognition.onresult = (e) => {
          let current = e.resultIndex;
          let userVoice = e.results[current][0].transcript;
          console.log(userVoice);

          if(userVoice === "go home") {
              goToHome();
          } else if(useVoice === "reservation") {
              goToReservation();
          } else if(useVoice === "table") {
              goToTable();
          } else {
              alert("Sorry, we are not support other voice instructions Sorry for your inconvenience ðŸ¤—");
          }
      };

      function goToHome() {
          window.location ="http://localhost:3000/Home";
      }
      function goToReservation() {
        window.location ="http://localhost:3000/Reservation";
      }

      function goToTable() {
        window.location ="http://localhost:3000/Table";
      }

      useEffect(()=> {
        voiceCommands();
      });
    return (

        <div>
            <h1> Welcome to Guu Garden! :)</h1>
            <Jumbotron />

        </div>

    )
}

export default Main;
